sys_script_client.script:
function onLoad() {
	//Type appropriate comment here, and begin script below

	// Run only on my tables, not all task tables
	var table = g_form.getTableName();
	if (table == 'x_542049_ideas_ideas_request' || 
		table == 'x_542049_ideas_spatial_transcriptomics') {
		// Instantiate the GetTfa Script Include 
		var getTFA = new GlideAjax('getTfa');
		// Specify the getTFA method
		getTFA.addParam('sysparm_name','getTFA');
		// Send the request to the server
		getTFA.getXML(populateChoiceList);
	}

    // When the response is back from the server
    function populateChoiceList(response){
        // Extract the choices from the response
        var tfaInJson = response.responseXML.documentElement.getAttribute("answer");
		var tfaList = tfaInJson.evalJSON();
        for (var i=0; i < tfaList.length; i++) {
			g_form.addOption('tfa_tech_center', tfaList[i], tfaList[i]);
		}
    }
}
