sys_script_include.script:
var getTfa = Class.create();
getTfa.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getTFA: function() {
		try { 
			var r = new sn_ws.RESTMessageV2('x_542049_ideas.GetTFA', 'Get TFA List');
			r.setMIDServer('sntest');
			//if the message is configured to communicate through ECC queue, either
			//by setting a MID server or calling executeAsync, one needs to set skip_sensor
			//to true. Otherwise, one may get an intermittent error that the response body is null
			r.setEccParameter('skip_sensor', true);
			var response = r.execute();
			var responseBody = response.getBody();
			var httpStatus = response.getStatusCode();
			var parser = new global.JSON();
			var tfasFromIdeas = parser.decode(responseBody);
			var tfalist = [];
			for (var i=0; i < tfasFromIdeas.length; i++) {
				tfalist.push(tfasFromIdeas[i]["type"]);
			}
			return JSON.stringify(tfalist);
		}
		catch(ex) {
			var message = ex.message;
			message;
		}
	},
    type: 'getTfa'
});
