sys_script_include.script:
function SendToEventBridge(detailType, data) {
	gs.info('SendToEventBridge: detailType=' + detailType);
	var ecc = new GlideRecord('ecc_queue');
	ecc.initialize();
	// It should be possible to assign a tag (like AWS) to a MID server and 
	// then find a MID server with that tag when making the request.
	// For now, just hard code the correct server.
	ecc.agent = 'mid.server.sntest';
	ecc.topic = "Command";
	ecc.queue = "output";
	var jsonData = JSON.stringify(data);
	gs.info('jsonData=' + jsonData);
	var entries = { 
		'Entries' : [
  {
    "EventBusName": "global-requests-staging",
    "Source": "ServiceNow",
    "DetailType": detailType,
    "Detail": jsonData
  }
]};
	gs.info('entries=' + JSON.stringify(entries));
	var cmd = "aws events put-events --region us-east-1 --cli-input-json '"  + JSON.stringify(entries) + "'";
	gs.info('cmd=' + cmd);
	ecc.payload='<parameters><parameter name="name" value="'+cmd+'"/></parameters>';
	ecc.insert();
	gs.info('SendToEventBridge: exiting');
}
